            chills = self.chills.isChecked()
            temperature = self.temperature.toPlainText()
            sweating = self.sweating.isChecked()
            body_pain = self.bodypain.isChecked()
            headache = self.headache.isChecked()
            yellow_skin = self.yellowskin.isChecked()
            skin_rash = self.skinrash.isChecked()
